mapper.select:
select t1.return_list_id, t1.return_number, t1.return_date, t1.amount_paid, t1.amount_payable, t1.remarks, t1.state, t1.supplier_id, t1.user_id, t2.supplier_name, t3.true_name from t_return_list t1 LEFT JOIN t_supplier t2 on t1.supplier_id = t2.supplier_id LEFT JOIN t_user t3 on t1.user_id = t3.user_id
AND t1.return_number LIKE CONCAT('%', #{returnNumber} ,'%')
AND t1.supplier_id = #{supplierId}
AND t1.state = #{state}
AND t1.return_date >= #{t1}
AND t1.return_date <= #{t2}

select t1.return_number as number, t1.return_date as date, t3.supplier_name as supplierName, t2.goods_code as code, t2.goods_name as name, t2.goods_model as model, t4.goods_type_name as goodsType, t2.goods_unit as unit, t2.price, t2.goods_num, t2.total
FROM t_return_list t1
LEFT JOIN t_return_list_goods t2 on t1.return_list_id = t2.return_list_id
LEFT JOIN t_goods_type t4 on t2.goods_type_id = t4.goods_type_id
LEFT JOIN t_supplier t3 ON t1.supplier_id = t3.supplier_id
and t1.return_date >= #{start}
AND t1.return_date <= #{end}
and t2.goods_type_id #{goodsTypeId}
and ( t2.goods_code = #{codeOrName} or t2.goods_name like CONCAT('%',#{codeOrName},'%') )
